# Factorial function (recursive)
&factorial($n) = {
    if $n <= 1 {
        return 1
    }
    return $n * &factorial($n - 1)
}

# Fibonacci function (recursive)
&fibonacci($n) = {
    if $n <= 0 {
        return 0
    }
    if $n == 1 {
        return 1
    }
    return &fibonacci($n - 1) + &fibonacci($n - 2)
}

# Test the functions
print("Factorial of 5: " + &str(&factorial(5)))
print("Fibonacci of 10: " + &str(&fibonacci(10)))

