# Login function
&login($username, $password) = {
    $user = %users[$username]

    if $user == none {
        return "User not found"
    }

    $stored_pass = $user["password"]

    if $password == $stored_pass {
        return "Login successful"
    }

    return "Invalid password"
}

# Test login
